svg.script:
// This runs when the SVG is parsed (not guaranteed in all viewers).
console.log('Inline <script> executed inside SVG');

// Example: expose a function and call it from an event handler
function handleCircleClick(evt) {
  const cx = evt.target.getAttribute('cx');
  const cy = evt.target.getAttribute('cy');
  console.log('Circle clicked at:', { cx, cy, time: new Date().toISOString() });
}
