svg.script:
function init(evt) {
  const svg = evt.target;
  console.log('SVG shown: onload fired');
  // Change visuals when shown
  const circle = svg.querySelector('#dot');
  const label = svg.querySelector('#label');
  // Example changes
  circle.setAttribute('fill', '#4CAF50'); // turn green
  label.textContent = 'Shown ✔';         // update text
}
